<?php
session_start();
echo "<h1 style='text-align:center'>AÑADE TUS PRODUCTOS AL CARRITO DE COMPRAS</h1>";
$productos = array(
    array('nombre' => 'CHEETOS', 'precio' => 12, 'imagen' => 'data:image/jpeg;base64,'),
    array('nombre' => 'COCA-COLA', 'precio' => 23, 'imagen' => 'https://las.comercialtrevino.com/wp-content/uploads/2021/08/832.jpg'),
    array('nombre' => 'AZUCAR', 'precio' => 38, 'imagen' => 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTMFZoKJBjf_Yuomx6GlnHhkuCHaF6X4LQ-cQ&usqp=CAU')
);

if(isset($_POST['submit'])) {
    $cantidad_productos = $_POST['cantidad'];
    $_SESSION['cantidad_productos'] = $cantidad_productos;

    $total_productos = 0;
    foreach($productos as $key => $producto) {
        $total_productos += $producto['precio'] * $cantidad_productos[$key];
    }
    $_SESSION['total_productos'] = $total_productos;
    header('Location: resumen.php');
}
?>
<!-- Agrego el código HTML para mostrar los productos y permitir al usuario comprar -->
<div style="width: 50%; margin: 0 auto; padding: 20px; background-color: #f2f2f2; border-radius: 5px; box-shadow: 0 2px 5px rgba(0, 0, 0, 0.3); text-align: center;">
    <form method="POST" action="">
        <?php foreach($productos as $key => $producto) { ?>
            <div style="margin: 10px 0;">
                <img src="<?php echo $producto['imagen']; ?>" alt="<?php echo $producto['nombre']; ?>" style="width: 100px; height: 100px; object-fit: cover;">
                <label style="font-size: 18px;"><?php echo $producto['nombre']; ?> - Precio: $<?php echo $producto['precio']; ?></label>
                <input type="number" name="cantidad[]" min="0" value="0" style="font-size: 16px; margin-left: 10px;">
            </div>
        <?php } ?>
        <input type="submit" name="submit" value="Comprar" style="font-size: 18px; padding: 10px 20px; margin-top: 20px; background-color: #4caf50; color: #fff; border: none; border-radius: 5px; cursor: pointer;">
    </form>
</div>
